/**
 * Represents the possible fillMode options of the DateInput.
 *
 * The possible values are:
 * - `solid` (Default)
 * - `flat`
 * - `outline`
 * - `none`
 */
export type DateInputFillMode = 'solid' | 'flat' | 'outline' | 'none';
