/**
 * Represents the options for the `checkedState` input of the CheckBox component.
 *
 * @example
 * ```html
 * <kendo-checkbox checkedState="indeterminate"></kendo-checkbox>
 * ```
 */
export type CheckBoxState = boolean | 'indeterminate';
